import React, { Component } from 'react';
import { Grid, InputBase, FormControlLabel, Switch } from '@material-ui/core'
import { fade, withStyles } from "@material-ui/core/styles";
import SearchIcon from '@material-ui/icons/Search';



const Styles = theme => ({
    search: {
        display: 'flex',
        alignItems: 'center',
        padding: theme.spacing(1),
        borderRadius: '28px',
        backgroundColor: fade(theme.palette.common.black, 0.50),
        '&:hover': {
            backgroundColor: fade(theme.palette.common.black, 0.75),
        },
        width: '50%',
        [theme.breakpoints.down('sm')]: {
            width: '80%',
        },
    },
    searchIcon: {
        width: theme.spacing(7),
        height: '100%',
        cursor: 'pointer',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        '&:hover': {
            color: '#ffb503'
        }
    },
    inputRoot: {
        color: 'inherit',
        // minWidth: '90%'
    },
    inputInput: {
        padding: theme.spacing(1, 1, 1, 7),
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            width: 220,
            '&:focus': {
                width: 350,
            },
        },
    },

})

class SearchQuestion extends Component {
    state = {
        typeChecked: false,
        levelChecked: false,
        categoryChecked: false,
        search: ''
    }
    handleChange = name => e => {
        this.setState({
            [name]: e.target.checked
        })
        console.log(this.state)
    }
    handleOnChange = e => {
        const { value } = e.target;
        this.setState({
            search: value
        })
        console.log(this.state.search)
    }
    render() {
        const { classes } = this.props;
        return (
            <div style={{ display:'flex'}}>
                <div className={classes.search}>
                    <div className={classes.searchIcon}>
                        <SearchIcon />
                    </div>
                    <div >
                        <InputBase
                            onChange={this.handleOnChange}
                            placeholder="Searchâ€¦"
                            classes={{
                                root: classes.inputRoot,
                                input: classes.inputInput,
                            }}
                            inputProps={{ 'aria-label': 'search' }}
                        />
                    </div>
                </div>
                <FormControlLabel
                    control={<Switch name="filter" value="filter" checked={this.state.filter} onChange={this.handleChange('filter')} color="primary" />}
                    label="Filter"
                />
            </div>
        );
    }
}

export default withStyles(Styles)(SearchQuestion);
