import React, { Component } from 'react';
import { Grid, InputBase, FormControlLabel, Switch } from '@material-ui/core'
import { fade, withStyles } from "@material-ui/core/styles";
import SearchIcon from '@material-ui/icons/Search';



const Styles = theme => ({
    search: {
        display: 'flex',
        alignItems: 'center',
        // justifyContent: 'center',
        padding: theme.spacing(1),
        // position: 'relative',
        borderRadius: '28px',
        backgroundColor: fade(theme.palette.common.black, 0.50),
        '&:hover': {
            backgroundColor: fade(theme.palette.common.black, 0.75),
        },
        margin: 'auto',
        maxWidth: '60%',
        [theme.breakpoints.up('sm')]: {
            // marginLeft: theme.spacing(1),
            idth: '80%',
        },
    },
    searchIcon: {
        width: theme.spacing(7),
        height: '100%',
        cursor: 'pointer',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        '&:hover': {
            color: '#ffb503' 
        }
    },
    inputRoot: {
        color: 'inherit',
        // minWidth: '90%'
    },
    inputInput: {
        padding: theme.spacing(1, 1, 1, 7),
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            width: 220,
            '&:focus': {
                width: 350,
            },
        },
    },

})

class SearchQuestion extends Component {
    state = {
        search: ''
    }
    handleChange = name => ({ target: { value } }) => {
        this.setState({
            search: value,
            [name]: value
        })
        console.log(this.state.search)
    }
    handleOnChange = e =>{
        // const {name,value} = e.target;
        this.setState({
            search: e.target.value
        })
        console.log(this.state.search)
    }
    render() {
        const { classes } = this.props;
        return (
            <div style={{ margin: 'auto' }}>
                <div className={classes.search}>
                    <div className={classes.searchIcon}>
                        <SearchIcon />
                    </div>
                    <div >
                    <InputBase
                            onChange={this.handleOnChange}
                        placeholder="Search…"
                        classes={{
                            root: classes.inputRoot,
                            input: classes.inputInput,
                        }}
                        inputProps={{ 'aria-label': 'search' }}
                    />
                    </div>
                </div>
                <Grid container style={{ maxWidth: '40%', margin: 'auto' }}>
                    <Grid item xs>
                        <FormControlLabel
                            control={<Switch name="type" value={this.state.checked} onChange={this.handleChange} color="primary" />}
                            label="by Type"
                        />
                    </Grid>
                    <Grid item xs>
                        <FormControlLabel
                            control={<Switch name="level" value={this.state.checked} onChange={this.handleChange} color="primary" />}
                            label="by Level"
                        />
                    </Grid>
                    <Grid item xs>
                        <FormControlLabel
                            control={<Switch name="category" value={this.state.checked} onChange={this.handleChange} color="primary" />}
                            label="by Category"
                        />
                    </Grid>
                </Grid>
            </div>
        );
    }
}

export default withStyles(Styles)(SearchQuestion);
