import React, { Component } from 'react';
import { Grid,InputBase, FormControlLabel, Switch } from '@material-ui/core'
import { fade, withStyles } from "@material-ui/core/styles";
import SearchIcon from '@material-ui/icons/Search';



const Styles = theme => ({
    search: {
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
        padding: theme.spacing(1),
        // position: 'relative',
        borderRadius: '28px',
        backgroundColor: '#011',
        // backgroundColor: fade(theme.palette.common.black, 0.15),
        '&:hover': {
            backgroundColor: fade(theme.palette.common.black, 0.75),
        },
        margin: 'auto',
        maxWidth: '60%',
        [theme.breakpoints.up('sm')]: {
            // marginLeft: theme.spacing(1),
            width: 'auto',
        },
    },
    searchIcon: {
        width: theme.spacing(7),
        height: '100%',
        cursor: 'pointer',
        display: 'flex',
        alignItems: 'center',
        justifyContent: 'center',
    },
    inputRoot: {
        color: 'inherit',
        minWidth: '90%'
    },
    inputInput: {
        padding: theme.spacing(1, 1, 1, 7),
        transition: theme.transitions.create('width'),
        width: '100%',
        [theme.breakpoints.up('sm')]: {
            width: 220,
            '&:focus': {
                width: '100%',
            },
        },
    },

})

class SearchQuestion extends Component {
    state = {
        checked: false
    }
    handleChange = name => ({ target: { value } }) => {
        this.setState({
            checked: !this.state.checked,
            [name]: value
        })
    }
    render() {
        const { classes } = this.props;
        return (
            <div style={{ textAlign: 'center' }}>
                <div className={classes.search}>
                    <div className={classes.searchIcon}>
                        <SearchIcon />
                    </div>
                    <InputBase
                        placeholder="Searchâ€¦"
                        classes={{
                            root: classes.inputRoot,
                            input: classes.inputInput,
                        }}
                        inputProps={{ 'aria-label': 'search' }}
                    />
                </div>
                <Grid container style={{maxWidth:'40%', margin:'auto'}}>
                    <Grid item xs>
                        <FormControlLabel
                            control={<Switch name="type" value={this.state.checked} onChange={this.handleChange} color="primary" />}
                            label="by Type"
                        />
                    </Grid>
                    <Grid item xs>
                        <FormControlLabel
                            control={<Switch name="level" value={this.state.checked} onChange={this.handleChange} color="primary" />}
                            label="by Level"
                        />
                    </Grid>
                    <Grid item xs>
                        <FormControlLabel
                            control={<Switch name="category" value={this.state.checked} onChange={this.handleChange} color="primary" />}
                            label="by Category"
                        />
                    </Grid>
                </Grid>
            </div>
        );
    }
}

export default withStyles(Styles)(SearchQuestion);
