import React, { Component } from 'react';
import {Button,Dialog,DialogActions,DialogTitle} from '@material-ui/core'
import { Delete } from '@material-ui/icons'
import { deleteQuestion } from '../../store/actions/bankAction';
import {connect } from 'react-redux'
import Slide from '@material-ui/core/Slide';

const Transition = React.forwardRef(function Transition(props, ref) {
    return <Slide direction="down" ref={ref} {...props} />;
});


class DeleteAlert extends Component {
    state={
        open:false
    }
    handleToggle = () => {
        this.setState({
            open: !this.state.open
        })
    }
    render() {
        const { question, deleteQuestion } = this.props;
        return (
            <div>
                <Button size="small" variant="outlined" onClick={this.handleToggle} style={{ color: '#d32f2f' }}>
                Delete 
                <Delete style={{ marginLeft: 10 }} />
                </Button>
                <Dialog
                    open={this.state.open}
                    TransitionComponent={Transition}
                    keepMounted
                    onClose={this.handleToggle}
                    // aria-labelledby="alert-dialog-slide-title"
                    // aria-describedby="alert-dialog-slide-description"
                >
                    <DialogTitle id="alert-dialog-slide-title">{"Are you sure you want to delete this question ?"}</DialogTitle>
                    <DialogActions>
                        <Button onClick={()=> deleteQuestion(question.id)} color="primary">
                           <div onClick={this.handleToggle}> Delete </div>
                        </Button>
                        <Button onClick={this.handleToggle} variant="de" color="secondary">
                            Cancel
                        </Button>
                    </DialogActions>
                </Dialog>
            </div>
        );
    }
}

export default connect(null,{deleteQuestion})(DeleteAlert);
