import React from 'react'
import { CssBaseline, Divider, Drawer, Tabs, Tab, Toolbar, Button, Typography, IconButton, Hidden, List, ListItem, ButtonGroup } from '@material-ui/core'
import PersonOutlineIcon from '@material-ui/icons/PersonOutline';
import MenuIcon from '@material-ui/icons/Menu'
import { makeStyles, useTheme } from '@material-ui/core/styles'
import { connect } from 'react-redux'
import { signOut } from '../../store/actions/authAction'
import { Link } from 'react-router-dom'
import logo from '../../assets/images/integrifyLogo.png'

const useStyles = makeStyles(theme => ({
    toolbar: {
        display: 'flex',
        justifyContent: 'space-between'
    },
    logo: {
        margin: 10,
        width: 100,
        height: 47
    },
    tabs: {
        flexGrow: 1,
        margin: 25,
        // borderRight: `1px solid ${theme.palette.divider}`,
    },
    menuItems: {
        textTransform: 'capitalize',
        fontSize: 16,
        color: '#fee'
    },
    nav: {
        margin: 'auto'
    },
    shortIndicator: {
        maxWidth: 60,
        marginLeft: theme.spacing(6),
    },
    controllers: {
        display: 'inline-flex',
        justifyContent: 'space-between',
        width: '180px'
    },
    drawer: {
        display: 'none',
        backgroundColor: '#001',
        [theme.breakpoints.up('sm')]: {
            width: drawerWidth,
            flexShrink: 0,
        },
    },
    Btns: {
        display: 'inline-flex',
        justifyContent: 'space-between',
        width: '250px',
        [theme.breakpoints.down('xs')]: {
            display: 'none',
        }
    },
    toolbar: theme.mixins.toolbar,
    drawerPaper: {
        width: drawerWidth,
    },
    menuButton: {
        color: 'white',
        '&:hover': {
            color: '#ffb503'
        },
        marginRight: theme.spacing(2),
        [theme.breakpoints.up('sm')]: {
            display: 'none',
        },
    },
    content: {
        flexGrow: 1,
        padding: theme.spacing(3),
    },
}));

const drawerWidth = 180;

const SignedInLinks = (props) => {
    const classes = useStyles()
    const { container } = props;
    const theme = useTheme();
    const [mobileOpen, setMobileOpen] = React.useState(false);

    const [value, setValue] = React.useState(0);

    function handleChange(event, newValue) {
        setValue(newValue);
    }
    function handleDrawerToggle() {
        setMobileOpen(!mobileOpen);
    }

    const drawer = (
        <div>
            <div className={classes.toolbar} />
            <Divider />
            <ButtonGroup style={{ display: 'flex', flexDirection: 'column' }} fullWidth>
                <Button component={Link} to="/" className={classes.menuItems}>home</Button>
                <Button >question bank</Button>
                <Button>about us</Button>
                <Button>contacts</Button>
            </ButtonGroup>
            <List>
                <ListItem>
                    <Button component={Link} to="/" className={classes.menuItems}>
                        home
                    </Button>
                </ListItem>
                <ListItem>
                    <Button component={Link} to="/questionBank" className={classes.menuItems}>
                        Question Bank
                    </Button>
                </ListItem>
                <ListItem>
                    <Button component={Link} to="/aboutUs" className={classes.menuItems}>
                        about us
                    </Button>
                </ListItem>
                <ListItem>
                    <Button component={Link} to="/contacts" className={classes.menuItems}>
                        contacts
                    </Button>
                </ListItem>
                {/* <Tab label="home" component={Link} to="/" className={classes.menuItems} />
                <Tab label="Question Bank" component={Link} to="/questionBank" className={classes.menuItems} />
                <Tab label="about us" component={Link} to="/aboutUs" className={classes.menuItems} />
                <Tab label="contacts" component={Link} to="/contacts" className={classes.menuItems} /> */}
            </List>
        </div>
    );

    return (
        <Toolbar className={classes.toolbar}>
            <CssBaseline />
            <div style={{ display: 'flex' }}>
                <div className={classes.controllers}>
                    <IconButton color="inherit" edge="start" onClick={handleDrawerToggle} className={classes.menuButton}>
                        <MenuIcon />
                    </IconButton>
                    <div>
                        <Link to="/">
                            <img alt="logo" src={logo} className={classes.logo} />
                        </Link>
                    </div>
                </div>
                <Tabs value={value} onChange={handleChange} className={classes.nav} classes={{ indicator: classes.shortIndicator }} indicatorColor="primary">
                    <Tab label="home" component={Link} to="/" className={classes.menuItems} />
                    <Tab label="Question Bank" component={Link} to="/questionBank" className={classes.menuItems} />
                    <Tab label="about us" component={Link} to="/aboutUs" className={classes.menuItems} />
                    <Tab label="contacts" component={Link} to="/contacts" className={classes.menuItems} />
                </Tabs>
            </div>
            <div style={{ display: 'flex', justifyContent: 'flex-end' }}>
                <Typography variant="h3" className={classes.menuItems} style={{ margin: 'auto 10px' }} gutterBottom>{props.profile.firstName} {props.profile.lastName}</Typography>
                <Button size="small" onClick={props.signOut} variant="outlined" color="primary" component={Link} to="/">
                    <PersonOutlineIcon style={{ marginRight: '10px' }} /> log out
                </Button>

                <nav className={classes.drawer}>
                    <Hidden smUp implementation="css">
                        <Drawer
                            container={container}
                            variant="temporary"
                            anchor={theme.direction === 'rtl' ? 'right' : 'left'}
                            open={mobileOpen}
                            onClose={handleDrawerToggle}
                            classes={{
                                paper: classes.drawerPaper,
                            }}
                            ModalProps={{
                                keepMounted: true, // Better open performance on mobile.
                            }}
                        >
                            {drawer}
                        </Drawer>
                    </Hidden>

                    <Hidden xsDown implementation="css">
                        <Drawer
                            classes={{
                                paper: classes.drawerPaper,
                            }}
                            variant="permanent"
                            open
                        >
                            {drawer}
                        </Drawer>
                    </Hidden>
                </nav>
            </div>
        </Toolbar>
    )
}

const mapStateToProps = state => {
    // console.log(state)
    return state;
}
const mapDispatchToProps = dispatch => {
    return {
        signOut: () => dispatch(signOut())
    }
}

export default connect(mapStateToProps, mapDispatchToProps)(SignedInLinks)
