//             {props.children}
//         </Typography>
//     );
// }

// TabContainer.propTypes = {
//     children: PropTypes.node.isRequired,
// };

const useStyles = makeStyles(theme => ({
    root: {
        flexGrow: 1,
        // backgroundColor: theme.palette.background.paper,
    },
    shortIndicator: {
        maxWidth: 60,
        marginLeft: theme.spacing(6),
    }
}));

function TabPanel(props) {
    const { children, value, index, ...other } = props;

    return (
        <Typography
            component="div"
            role="tabpanel"
            hidden={value !== index}
            id={`simple-tabpanel-${index}`}
            aria-labelledby={`simple-tab-${index}`}
            {...other}
        >
            <Box p={3}>{children}</Box>
        </Typography>
    );
}

const QuestionBank = (props) => {
    console.log(props)
    const classes = useStyles();
    const [value, setValue] = React.useState(0);

    function handleChange(event, newValue) {
        setValue(newValue);
    }

    const { questions, auth } = props
    // if (!auth.uid) return <Redirect to="/signin" />
    return (
        <div value={value} className={classes.root}>
            <AppBar position="static" color>
                <Tabs value={value} onChange={handleChange} classes={{ indicator: classes.shortIndicator }} indicatorColor="secondary">
                    <Tab label="CREATE"  />
                    <Tab label="SEARCH" />
                    <Tab label="STATESTICS" />
                </Tabs>
            </AppBar>
            {/* {value === 0 && <TabContainer>
                <CreateQuestion />
                <QuestionList
                questions={questions}
                />
            </TabContainer>}
            {value === 1 && <TabContainer>Publish here!</TabContainer>} */}
            <TabPanel value={value} index={0}>
                <CreateQuestion />
                <QuestionList
                    questions={questions}
                />
            </TabPanel>
            <TabPanel value={value} index={1}>
                search
            </TabPanel>
            <TabPanel value={value} index={2}>
                statestics
            </TabPanel>
        </div>
    )
}

const mapStateToProps = state => {
    return {
        questions: state.bank.questions,
        // auth: state.firebase.auth
    }
}

// export default compose(
//     connect(mapStateToProps),
//     firestoreConnect([
//         {collection: 'questions'}
//     ])
// )(QuestionBank)

export default connect(mapStateToProps)(QuestionBank)
