import React, { Component } from 'react'
// import moment from 'moment'
import { Card, CardActions, CardContent, Button, Typography } from '@material-ui/core'
import { Edit, Delete } from '@material-ui/icons'
import { withStyles } from '@material-ui/core/styles'
// import { deleteQuestion } from '../../store/actions/bankAction';
// import { connect } from 'react-redux'
// import { compose } from 'redux'
import DeleteAlert from '../Alerts/DeleteAlert';


const styles = theme => ({
    card: {
        display: 'flex',
        flexWrap: 'wrap',
        maxWidth: '60%',
        margin: 'auto',
        marginBottom: '12px',
        background: '#011'
    },
    title: {
        fontSize: 14,
    },
    pos: {
        // marginBottom: 12,
    },
});


class QuestionSummary extends Component {

    render() {
        const { question, classes } = this.props;
        console.log(question)
        return (
            <Card className={classes.card}>
                <CardContent style={{ flex: 3 }}>
                    <Typography className={classes.title} color="textSecondary" gutterBottom>
                        question :
                </Typography>
                    <Typography variant="h5" component="h2">
                        {question.question}
                        {question.id}
                    </Typography>
                    <Typography className={classes.title} color="textSecondary" gutterBottom>
                        Type :
                </Typography>
                    <Typography variant="h5" component="h2">
                        {question.questionType}
                    </Typography>
                    <Typography className={classes.title} color="textSecondary" gutterBottom>
                        Level :
                </Typography>
                    <Typography variant="h5" component="h2">
                        {question.questionLevel}
                    </Typography>
                    <Typography className={classes.title} color="textSecondary" gutterBottom>
                        Category :
                </Typography>
                    <Typography variant="h5" component="h2">
                        {question.questionCategory}
                    </Typography>
                    <Typography className={classes.title} color="textSecondary" gutterBottom>
                        Answer :
                </Typography>
                    <Typography variant="h5" component="h2">
                        {question.answer}
                    </Typography>
                    <Typography className={classes.pos} color="textSecondary">
                        Author :
                </Typography>
                    <Typography variant="body2" component="p">
                        Mostafa Hazareh
                </Typography>
                    <Typography className={classes.pos} color="textSecondary">
                        {/* {moment(question.createdAt.toDate()).calendar()} */}
                        12.07.2019
                </Typography>
                </CardContent>
                <CardActions style={{ flex: 1, alignSelf: 'flex-start', marginTop: 20, marginBottom: 10 }}>
                    <Button size="small" variant="outlined" color="primary" >Edit <Edit style={{ marginLeft: 10 }} /></Button>
                    {/* <Button size="small" variant="outlined" onClick={() => { deleteQuestion(question.id) }} style={{ color: 'red' }}>Delete <Delete style={{ marginLeft: 10 }} /></Button> */}
                    <DeleteAlert question={question}/>                
                </CardActions>
            </Card>
        )
    }

}

// export default compose(
//     withStyles(styles),
//     connect(null, { deleteQuestion })
// )(QuestionSummary)
export default withStyles(styles)
